import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import {
  deleteKiteLesson,
  GetKiteLessons,
} from "../services/KiteLessonService";

export const KiteLessonsListComponent = () => {
  const [KiteLessons, setKiteLessons] = useState([]);

  useEffect(() => {
    getAllKiteLessons();
  }, []);

  const getAllKiteLessons = () => {
    GetKiteLessons()
      .then((response) => {
        setKiteLessons(response.data);
        console.log(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const deleteLesson = (id) => {
    deleteKiteLesson(id)
      .then((response) => {
        getAllKiteLessons();
      })
      .catch((error) => {
        console.log(error);
      });
  };

  return (
    <div className="container">
      <h2 className="text-center" style={{ marginTop: "15px" }}>
        Kite Lessons List
      </h2>
      <Link
        to="/api/v1/add-kite-lesson"
        className="btn btn-primary mb-2"
        style={{ marginbottom: "20px" }}
      >
        Add Kite Lesson
      </Link>
      <table className="table table-bordered table-striped">
        {/* Columns' names */}
        <thead>
          <th>Kite Lesson ID</th>
          <th>Kite Lesson Type</th>
          <th>Number of Persons</th>
          <th>Kite Lesson Duration</th>
          <th>Kite Lesson Price</th>
          <th>Actions</th>
        </thead>

