              <td>{lesson.id}</td>
              <td>{lesson.type}</td>
              <td>{lesson.persons}</td>
              <td>{lesson.hours}</td>
              <td>{lesson.price}</td>
              <td>
                <Link
                  className="btn btn-info"
                  to={`/api/v1/update-kite-lesson/${lesson.id}`}
                >
                  Update
                </Link>
                <button
                  className="btn btn-danger "
                  onClick={() => {
                    deleteLesson(lesson.id);
                  }}
                  style={{ marginLeft: "10px" }}
                >
                  Delete
